// import uuid from 'uuid/v4';

export const Reducer = (state, action) => {
  switch (action.type) {
    case 'ADD_Toll':
      console.log(action);
      return [
        ...state,
        {
          title: action.datas.title,
          author: action.datas.author,

        }
      ];



  }
};
