import React from "react";
import "../../App.css"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFilter, faXmark} from "@fortawesome/free-solid-svg-icons";


const options = [
    {
      label: "Car/Jeep/Van",
      value: "Car/Jeep/Van",
    },
    {
      label: "LCV",
      value: "LCV",
    },
    {
      label: "Truck/Bus",
      value: "Truck/Bus",
    },
    {
      label: "Heavy vehicle",
      value: "Heavy vehicle",
    },
  ];


export default function VehicleForm({vehicle,children,onClose}) {
    if (!vehicle) return null;
    return(
        <div>
            <div className="vehicle-form-container">
               <div className="vehicle-form-card" >
              <div style={{justifyContent:"end",display:"flex"}}>
               <FontAwesomeIcon  onClick={onClose} icon={faXmark} />
               </div>
                <p style={{textAlign:"center"}}>Add new entry</p>
                <form>
                   <div style={{display:"flex",flexDirection:"column"}}>
                    <label className="label-text">
                        Select toll Name
                    </label>
                    <select className="input-field">


            {options.map((option) => (

              <option value={option.value}>{option.label}</option>

            ))}

          </select>
                    </div>
                    <div style={{display:"flex",flexDirection:"column", marginTop:"8px"}}>
                    <label>
                        Vehicle Fare Details
                    </label>
                    <div style={{display:"flex",justifyContent:"space-between",marginBottom:"14px"}}>
                    <select className="input-field">

            {options.map((option) => (

              <option value={option.value}>{option.label}</option>

            ))}

          </select>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    </div>
                    <div style={{display:"flex",justifyContent:"space-between",marginBottom:"14px"}}>
                    <select className="input-field">


            {options.map((option) => (

              <option value={option.value}>{option.label}</option>

            ))}

          </select>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    </div>
                    <div style={{display:"flex",justifyContent:"space-between",marginBottom:"14px"}}>
                    <select className="input-field">

            {options.map((option) => (

              <option value={option.value}>{option.label}</option>

            ))}

          </select>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    </div>
                    <div style={{display:"flex",justifyContent:"space-between",marginBottom:"14px"}}>
                    <select className="input-field">


            {options.map((option) => (

              <option value={option.value}>{option.label}</option>

            ))}

          </select>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    <input className="input-field" placeholder="Enter toll name" type="number"/>
                    </div>
                    </div>
                    <button className="btn-add">
                        Add details
                    </button>
                </form>

                </div>
            </div>


        </div>
    )
}
