import uuid from 'uuid/v4';

export const Reducer = (state, action) => {
  switch (action.type) {
    case 'ADD_Toll':
      console.log(action);
      return [
        ...state,
        {
          title: action.book.title,
          author: action.book.author,
          id: uuid()
        }
      ];
    case 'REMOVE_Toll':
      console.log(action.id);
      return state.filter(book => book.id !== action.id);
    default:
      return state;
  }
};
