  function setTollData(toll) {
    dispatch({
      type: "SET_TOLL",
      payload: toll,
    });
  }

  function setVehicleData(vehicle) {
    dispatch({
      type: "SET_VEHICLE",
      payload: vehicle,
    });
  }







  return (
    <GlobalContext.Provider
      value={{
        tollData: state.tollData,
        vehicleData: state.vehicleData,


        setTollData,
        setVehicleData,

      }}
    >
      {children}
    </GlobalContext.Provider>
  );
};
